import React, {useState } from 'react';

function Square({onClick, isValueX}) {

    const [value, setValue] = useState(null);

    function handleClick() {
        onClick();
        setValue(isValueX ? "X" : "O");
    }

    return <button className="square" onClick={handleClick}>{value}</button>
}

export default function Board() {

    const [isValueX, setIsValueX] = useState(true);

    function handleClickToChangeSelection() {

        setIsValueX(!isValueX)
    }

    return (
        <>
            <div className="board-row">
                <Square isValueX={isValueX} onClick={handleClickToChangeSelection} />
                <Square isValueX={isValueX} onClick={handleClickToChangeSelection} />
